var fs = require('fs');

function isInArray(arr, matchPattern) {
  var found = false;
  arr.forEach(function (el) {
    if (!found) {
      found = el.match(matchPattern)
    }
  });
  return found;
}

function readJSON(name) {
  return JSON.parse(fs.readFileSync(name, 'utf8'));
}

function writeJSON(name, pkg) {
  fs.writeFileSync(name, JSON.stringify(pkg, null, 2) + '\n', 'utf8');
}

module.exports = {
  isInArray: isInArray,
  readJSON: readJSON,
  writeJSON: writeJSON
};
