#!/usr/bin/env node
'use strict';

const program = require('commander');

program.version(require('../package').version)
  .command('new [options]', 'create a new spec definition')
  .command('build  [options]', 'generate json for spec feeder')
  .command('update [version] [options]', 'make spec feeder refresh from artifactory')
  .parse(process.argv);
